    function mouseCoords(evt) {
        if (evt.pageX || evt.pageY) {
            return {x: evt.pageX, y: evt.pageY};
        }
        return {
            x:
                evt.clientX + me.document.body.scrollLeft - me.document.body.clientLeft,
            y: evt.clientY + me.document.body.scrollTop - me.document.body.clientTop
        };
    }

    function mouseMoveEvent(evt) {
        if (isEditorDisabled()) {
            return;
        }

        try {
            //普通状态下鼠标移动
            var target = getParentTdOrTh(evt.target || evt.srcElement),
                pos;

            //区分用户的行为是拖动还是双击
            if (isInResizeBuffer) {
                me.body.style.webkitUserSelect = "none";

                if (
                    Math.abs(userActionStatus.x - evt.clientX) > offsetOfTableCell ||
                    Math.abs(userActionStatus.y - evt.clientY) > offsetOfTableCell
                ) {
                    clearTableDragTimer();
                    isInResizeBuffer = false;
                    singleClickState = 0;
                    //drag action
                    tableBorderDrag(evt);
                }
            }

            //修改单元格大小时的鼠标移动
            if (onDrag && dragTd) {
                singleClickState = 0;
                me.body.style.webkitUserSelect = "none";
                me.selection.getNative()[
                    browser.ie9below ? "empty" : "removeAllRanges"
                    ]();
                pos = mouseCoords(evt);
                toggleDraggableState(me, true, onDrag, pos, target);
                if (onDrag == "h") {
                    dragLine.style.left = getPermissionX(dragTd, evt) + "px";
                } else if (onDrag == "v") {
                    dragLine.style.top = getPermissionY(dragTd, evt) + "px";
                }
                return;
            }
            //当鼠标处于table上时，修改移动过程中的光标状态
            if (target) {
                //针对使用table作为容器的组件不触发拖拽效果
                if (me.fireEvent("excludetable", target) === true) return;
                pos = mouseCoords(evt);
                var state = getRelation(target, pos),
                    table = domUtils.findParentByTagName(target, "table", true);

                if (inTableSide(table, target, evt, true)) {
                    if (me.fireEvent("excludetable", table) === true) return;
                    me.body.style.cursor =
                        "url(" + me.options.cursorpath + "h.png),pointer";
                } else if (inTableSide(table, target, evt)) {
                    if (me.fireEvent("excludetable", table) === true) return;
                    me.body.style.cursor =
                        "url(" + me.options.cursorpath + "v.png),pointer";
                } else {
                    me.body.style.cursor = "text";
                    var curCell = target;
                    if (/\d/.test(state)) {
                        state = state.replace(/\d/, "");
                        target = getUETable(target).getPreviewCell(target, state == "v");
                    }
                    //位于第一行的顶部或者第一列的左边时不可拖动
                    toggleDraggableState(
                        me,
                        target ? !!state : false,
                        target ? state : "",
                        pos,
                        target
                    );
                }
            } else {
                toggleDragButton(false, table, me);
            }
        } catch (e) {
            showError(e);
        }
    }

