	$(document).on("click", "button,.btn,.btnwave,.nav-link,.menu-item,.dropdown-item", function(e) {
		var elem = $(this);
		if (elem.hasClass("nowave") || elem.is(":disabled") || elem.hasClass("is-disabled")) {
			return;
		}
		var a;
		elem.css({
			position: "relative",
			overflow: "hidden"
		});
		var i = ~~elem.outerWidth()
          , n = ~~elem.outerHeight();
        a = i < n ? n : i;

		var rippleColor = elem.css("color") || "rgba(200, 200, 200, 0.3)";

		var ripple = $("<div>").css({
			display: "block",
			position: "absolute",
			"border-radius": "50%",
			background: rippleColor,
			"pointer-events": "none",
			"z-index": 1,
			transform: "scale(0)",
			"-webkit-transform": "scale(0)",
			opacity: 0.3,
			transition: "all 1.5s cubic-bezier(0.22, 0.61, 0.36, 1)",
			"-webkit-transition": "all 1.5s cubic-bezier(0.22, 0.61, 0.36, 1)",
			overflow: "hidden"
		});

		elem.append(ripple);
		ripple.css({
			width: 2 * a + "px",
            height: 2 * a + "px",
            top: e.pageY - elem.offset().top - a + "px",
            left: e.pageX - elem.offset().left - a + "px",
			transform: "scale(1)",
			"-webkit-transform": "scale(1)",
			opacity: 0
		});
		setTimeout(function() {
			ripple.remove();
		}, 2e3);
	});

