// 参考国外模板的写法，获取当前的配置，以data-*（*指插件原有的配置名）
getDataOptions = function(el, castList) {
    var options = {};
    $.each( $(el).data(), function(key, value){
        key = dataToOption(key);
        if ( key == 'provide' ) {
            return;
        }
        options[key] = value;
    });
    
    return options;
}

dataToOption = function(name) {
    return name.replace(/-([a-z])/g, function(x){return x[1].toUpperCase();});
}
function popbox() {
	$(".popover").css({display: "none"})
}
(function($) {
	$.fn.popbox = function(options) {
		var box = $('.popover');
		if(!box.length) {
			box = $(' <div class="popover fade clearfix">' +
				'  <div class="popover-arrow">' +
				'  </div>' +
				'  <div class="popover-container">' +
				'	   <button class="btn" type="button" disabled><span class="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>加载中...</button>' +
				'  </div>' +
				'</div>'
			).appendTo('body');
		}
		var me = $(this);

		var dataurl = [];
		var current = 0;
		var open = function(event) {
			if(event) event.preventDefault();
			if(me.data('href')) {
				getdata(me.data('data-href'));
			}
			$('.js-popbox').removeClass('openpop');
			show();
			if(me.data('closest')) {
				me.closest(me.data('closest')).find('.dropdown-toggle').dropdown('toggle');
			}
			return false;
		}
		var show = function() {
			var clientWidth = document.documentElement.clientWidth;
			var clientHeight = document.documentElement.clientHeight;
			if(me.data('closest')) var target = me.closest(me.data('closest'));
			else var target = me;

			me.addClass('openpop');
			var p = target.offset();
			var bw = box.outerWidth(true);
			var bh = box.outerHeight(true);
			var w = target.outerWidth(true);
			var h = target.outerHeight(true);
			var left = 0,top = 0;
			box.attr('class', 'popover fade show bs-popover-' + me.data('placement'));
			switch(me.data('placement')) {
				case 'cover':
					left = p.left;
					top = p.top;
					break;
				case 'right':
					left = p.left + w + 10;
					top = p.top + h / 2 - bh / 2;
					break;
				case 'top':
					top = p.top - bh - 10;
					left = p.left + w / 2 - bw / 2;
					break;
				case 'bottom':
					top = p.top + h + 10;
					left = p.left + w / 2 - bw / 2;
					break;
				case 'left':
					left = p.left - bw - 10;
					top = p.top + h / 2 - bh / 2;
					break;
				default:
					left = p.left + w + 10;
					top = p.top + h / 2 - bh / 2;
					break;
			}

			//判断是否超出屏幕
			if(me.data('auto-adapt')) {
				switch(me.data('placement')) {
					case 'right':
					case 'left':
						if(left + bw > clientWidth) left = clientWidth - bw - 10;
						if(left < 0) left = 10;
						if(top + bh > (clientHeight + jQuery(window).scrollTop())) top = clientHeight + jQuery(window).scrollTop() - bh - 10;
						if(top < 0) top = 10;
						break;
					case 'top':
					case 'bottom':
						if(top + bh > (clientHeight + jQuery(window).scrollTop()) && (me.data('placement') != 'bottom' && me.data('placement') != 'cover')) top = clientHeight - bh - 10 + jQuery(window).scrollTop();
						if(top < 0) top = 10;
						if(left + bw > clientWidth) left = clientWidth - bw - 10;
						if(left < 0) left = 10;
						break;
					case 'cover':
						if(left + bw > clientWidth) left = clientWidth - bw - 10;
						if(left < 0) left = 10;
						break;
				}
			}

			box.css({'display': 'block','left': left,'top': top});
			switch(me.data('placement')) {
				case 'right':
				case 'left':
					var top = p.top - top + h / 2 - 10;
					if(top + 25 > bh) top = bh - 25;
					else if(top < 5) top = 5;
					box.find('.popover-arrow').attr('style', 'top:' + (top) + 'px');
					break;
				case 'top':
				case 'bottom':
					var left = p.left - left + w / 2 - 10;
					if(left + 25 > bw) left = bw - 25;
					else if(left < 5) left = 5;
					box.find('.popover-arrow').attr('style', 'left:' + left + 'px');
					break;
			}
			window.addEventListener('mousedown', function(event) {
				if (!$(event.target).closest('.popover,.js-popbox,.ui-icon,.dzzdate,.ui-corner-all,.datepicker', '#jquery-color-picker').length) {
					close();
					$('.openpop').removeClass('openpop');
				}
			});
		}
		
		var getdata = function(url) {
			if(!url) url = me.data('href');
			if(url == '#'){
				var html = me.siblings('.popover-html').html();
				data_operate(html);
			}else{
				var s=(url.indexOf('?') != -1) ? '&' :'?';
				url+=s+'t='+new Date().getTime();
				current = dataurl.push(url);
				$.get(url, function(html) {
					data_operate(html);
				});
			}
		}
		var data_operate = function(html){
			box.find('.popover-container').html(html);
			box.find('.js-popbox').on('click', function() {
				getdata($(this).data('href'));
				return false;
			});
			box.find('.js-popbox-prev').on('click', function() {
				goto_prev()
				return false;
			});
			box.find('.close,.cancel').on('click', function(event) {
				event.preventDefault();
				close();
			});
            box.find('.btn-close,.cancel').on('click', function(event) {
				event.preventDefault();
				close();
			});
			show();
		}
		var goto_prev = function() {
			if(current > 1) {
				current -= 1;
			} else {
				current = 0;
			}
			url = dataurl[current - 1];
			dataurl.splice(current - 1, dataurl.length - current + 1);
			getdata(url);
		}
		var close = function() {
			current = 0;
			dataurl = [];
			box.data('prevel', null);
			box.fadeOut("fast", function() {
				box.find('.popover-container').html('<button class="btn" type="button" disabled><span class="spinner-border spinner-border-sm" role="status" aria-hidden="true"></span>加载中...</button>');
			});
			me.removeClass('openpop');
			$(document).off('click.popover');
		}
		if(options == 'update') {
			window.setTimeout(function() {
				show();
			});
		} else if(options == 'open') {
			window.setTimeout(function() {
				open();
			});
		} else if(options == 'close') {
			close();
		} else if(options == 'getdata') {
			getdata();
		} else {
			$(this).off('click.popover').on('click.popover', open);
		}
	}
	$.fn.leftDrager_layout = function()
	{
		var $this=$(this);
		var $leftContainer=$('.bs-left-container');
		var $mainContainer=$('.bs-main-container');
		var $topContainer=$('.bs-top-container');
		var oleft=0;
		var left=oleft;
		var clientWidth = document.documentElement.clientWidth;
		var setPosition=function(xx,flag){
			xx=xx*1;
			var mainWidth=$mainContainer.outerWidth(true);
			var minWidth=parseInt($mainContainer.css('minWidth')) ||0;
			if(mainWidth-xx<minWidth){
				xx=mainWidth-minWidth;
			}
			
			left=xx;
			if(flag===true){
				oleft=left;
			} 
			$leftContainer.css('width',left);
			$mainContainer.css('paddingLeft',left);
			$topContainer.css('paddingLeft',left);
			var currentRightWidth = mainWidth - xx;
			$mainContainer.trigger('leftDrager_layout.changeWidthValue',[currentRightWidth]);
			$this.css('left',left);
		};
		var dragging=function(){
			$this.off('mousedown').on('mousedown',function(e){
				 e.preventDefault(); 
				 var x=e.clientX;
				 var ox=x-$this.offset().left;
				 var width=$this.width();
				 $(document).mousemove(function(e){
					  e.preventDefault();
					  var xx=e.clientX;
					  if((xx-ox+width)>clientWidth){
						  xx=clientWidth+ox-width;
					  }
					  if(xx-ox<=0){
						xx=ox;  
					  } 
					  setPosition(xx-ox);
				 });
				 $(document).mouseup(function(e) {
					 $(document).off('mouseup').off('mousemove');
					 var xx=e.clientX;
					  if((xx-ox+width)>clientWidth){
						  xx=clientWidth+ox-width;
					  }
					  if(xx-ox<=0){
						xx=ox;  
					  }
					  setPosition(xx-ox,true);
					  
				 });
			});
		};
		dragging();
	};
})(jQuery);
if (jQuery('.left-drager').length) {
	jQuery('.left-drager').leftDrager_layout();
}